INSERT INTO `pages` VALUES ('9f4cc3a1-da58-42f4-b59c-74aaadf1b222', 'fastboat-ekajaya', 'feature-fastboat-ekajaya', '<div class=\"w-full max-w-7xl mx-auto pt-10 px-2 pb-64 md:pb-32\">\r\n    <div class=\"flex flex-col\">\r\n        <div class=\"flex flex-row justify-between\">\r\n            <div class=\"flex flex-col\">\r\n                <h1 class=\"text-3xl font-bold\">Premium Ekajaya Fastboat Services</h1>\r\n                <p class=\"text-gray-400\">Tiket Online proudly offers Ekajaya Fastboat services, ensuring fast and dependable connections among Bali, Lombok, Nusa Lembongan, Nusa Penida, Nusa Ceningan, and the Gili Islands. Experience the beauty of Indonesia with our first-class Ekajaya Fastboat services, designed for your comfort and enjoyment.</p>\r\n                <h2 class=\"text-2xl font-semibold mt-4\">Selecting Ekajaya Fastboat</h2>\r\n                <p class=\"text-gray-400\">Ekajaya Fastboat is a premier fast boat operator in the region, known for its quickness, safety, and exceptional service. The modern vessels are engineered to deliver a seamless and pleasurable journey to your favorite destinations, such as Gili Trawangan, Gili Air, Gili Meno, and Lombok.</p>\r\n                <h3 class=\"text-xl font-semibold mt-4\">Advantages of Reserving with Tiket Online:</h3>\r\n                <ul class=\"list-disc list-inside\">\r\n                    <li>Effortless online reservation for Ekajaya Fastboat tickets</li>\r\n                    <li>Affordable prices and unbeatable offers</li>\r\n                    <li>Customized support from our experienced travel specialists</li>\r\n                    <li>Exclusive deals and markdowns for our esteemed clientele</li>\r\n                </ul>\r\n                <h3 class=\"text-xl font-semibold mt-4\">Ekajaya Fastboat Features</h3>\r\n                <p class=\"text-gray-400\">Experience exceptional quality with Ekajaya Fastboat\'s aluminum vessels built to international standards. Key features include:</p>\r\n                <ul class=\"list-disc list-inside\">\r\n                    <li>4x Volvo D16 750 HP engines for high speed and comfort</li>\r\n                    <li>Accredited captain and crew</li>\r\n                    <li>Capacity for 210 passengers</li>\r\n                    <li>Fully air-conditioned cabin</li>\r\n                    <li>Spacious and furnished with high-quality seating</li>\r\n                    <li>Sun deck for a relaxing experience</li>\r\n                    <li>Comprehensive safety equipment</li>\r\n                    <li>Modern entertainment system with LCD TV and DVD player</li>\r\n                    <li>Advanced GPS technology and ship-to-shore radio</li>\r\n                    <li>Life rafts and fire extinguishers for added safety</li>\r\n                    <li>Onboard toilet facilities</li>\r\n                </ul>\r\n\r\n\r\n                <h3 class=\"text-xl font-semibold mt-4\">Journey in Luxury and Elegance</h3>\r\n                <p class=\"text-gray-400\">Experience the ease of fast boat services from Bali to Gili Trawangan, Gili Air, Gili Meno, and Lombok, with several daily departures. Ekajaya\'s advanced and well-maintained fleet guarantees a comfortable and stress-free voyage, allowing you to unwind and appreciate the stunning scenery.</p>\r\n\r\n                <h3 class=\"text-xl font-semibold mt-4\">Popular Fastboat Itineraries</h3>\r\n                <p class=\"text-gray-400\">Ekajaya Fastboat serves in-demand routes, such as:</p>\r\n                <ul class=\"list-disc list-inside\">\r\n                    <li>Bali to Gili Trawangan, Gili Air, Gili Meno, and Lombok</li>\r\n                    <li>Gili Trawangan to Bali, Gili Air, Gili Meno, and Lombok</li>\r\n                    <li>Gili Air to Bali, Gili Trawangan, Gili Meno, and Lombok</li>\r\n                    <li>Lombok to Bali, Gili Trawangan, Gili Air, and Gili Meno</li>\r\n                </ul>\r\n                \r\n                <p class=\"text-gray-400 mt-4\">Set sail on an extraordinary island-hopping escapade with Tiket Online and Ekajaya Fastboat now!</p>\r\n            </div>\r\n        </div>\r\n    </div>\r\n    <div class=\"h-24 md:h-16\"></div>\r\n</div>', NULL, NULL, 0, '2025-07-03 12:28:04', '2025-07-03 12:28:04', NULL, NULL, NULL, NULL, NULL, NULL);
