INSERT INTO `tour_packages` VALUES ('9f4cc3a1-b827-4a0c-a369-ca3b3f176ca9', 'PENIDA DAY TOUR ( WEST )', 'penida-day-tour-west', 'PENIDA DAY TOUR ( WEST )', '<p><span style=\"font-size: 14pt;\"><strong>What to expects :&nbsp;</strong></span></p>\r\n<p>1. Destination Angel Billabong, Broken Beach &amp; Kelingking Secret Point<br>2. Return fast boat transfers<br>3. Entrance fee<br>4. Lunch Set menu at Green Kubu<br>5. Private car and driver (max 6 pax / car)<br>Schedule :&nbsp;</p>\r\n<ul>\r\n<li>&nbsp; &nbsp;- departure 07.30 a.m ( serangan port )</li>\r\n<li>&nbsp; &nbsp;- return 15.00 p.m ( penida port )</li>\r\n</ul>\r\n<p><img src=\"../../images/post1.webp\" alt=\"My alt text\" width=\"377\" height=\"251\"></p>', 'PENIDA DAY TOUR ( WEST )', 475000.00, 'images/post4.webp', 1, '2025-07-03 12:28:04', '2025-07-03 12:28:04', NULL, NULL, NULL, NULL);
INSERT INTO `tour_packages` VALUES ('9f4cc3a1-baac-4ebf-8e7d-5f99d9ba2020', 'PENIDA DAY TOUR ( EAST )', 'penida-day-tour-east', 'PENIDA DAY TOUR ( EAST )', '<p><span style=\"font-size: 14pt;\"><strong>What to expects :&nbsp;</strong></span></p>\r\n<p>1. Destination Diamond Beach, Atuh Beach, Teletubies Hills, Tree House<br>2. Return fast boat transfers<br>3. Entrance fee<br>4. Lunch Set menu at Local Restaurant<br>5. Private car and driver (max 6 pax / car)<br>Schedule : - departure 07.30 a.m ( serangan port )<br>- return 15.00 p.m ( penida port )</p>\r\n<p>&nbsp;</p>', 'PENIDA DAY TOUR ( EAST )', 475000.00, 'images/post3.webp', 1, '2025-07-03 12:28:04', '2025-07-03 12:28:04', NULL, NULL, NULL, NULL);
INSERT INTO `tour_packages` VALUES ('9f4cc3a1-bcca-42c4-a43c-99ab1205f697', 'PENIDA DAY TOUR ( WEST - EAST )', 'penida-day-tour-west-east', 'PENIDA DAY TOUR ( WEST - EAST )', '<p><span style=\"font-size: 14pt;\"><strong>What to expects :&nbsp;</strong></span></p>\r\n<p>1. Destination Diamond Beach, Atuh Beach, Kelingking, Broken beach, Angel billabong<br>2. Return fast boat transfers<br>3. Entrance fee<br>4. Lunch Set menu at Local Restaurant<br>5. Private car and driver (max 6 pax / car)<br>Schedule : - departure 07.30 a.m ( serangan port )<br>- return 15.00 p.m ( penida port )</p>\r\n<p>&nbsp;</p>', 'PENIDA DAY TOUR ( WEST - EAST )', 540000.00, 'images/post3.webp', 1, '2025-07-03 12:28:04', '2025-07-03 12:28:04', NULL, NULL, NULL, NULL);
INSERT INTO `tour_packages` VALUES ('9f4cc3a1-beb2-4056-8a2e-7fbd53623206', 'PENIDA WEST TOUR - LEMBONGAN TOUR', 'penida-west-tour-lembongan-tour', 'PENIDA WEST TOUR - LEMBONGAN TOUR', '<p><span style=\"font-size: 14pt;\"><strong>What to expects :&nbsp;</strong></span></p>\r\n<p>1. Destination Kelingking Secret Point, Broken beach, Angel billabong, Dream<br>Beach, Devil Tears<br>2. Return fast boat transfers<br>3. Entrance fee<br>4. Lunch Set menu at Local Restaurant<br>5. Private car and driver (max 6 pax / car)<br>Schedule : - departure 07.30 a.m ( serangan port )<br>- return 15.00 p.m ( penida port )</p>\r\n<p>&nbsp;</p>', 'PENIDA WEST TOUR - LEMBONGAN TOUR', 590000.00, 'images/post2.webp', 1, '2025-07-03 12:28:04', '2025-07-03 12:28:04', NULL, NULL, NULL, NULL);
INSERT INTO `tour_packages` VALUES ('9f4cc3a1-c09a-4dc7-9110-94132407341b', 'PENIDA WEST TOUR - SNORKLING ( at QUICKSILVER MEGA PONTON)', 'penida-west-tour-snorkling-at-quicksilver-mega-ponton', 'PENIDA WEST TOUR - SNORKLING ( at QUICKSILVER MEGA PONTON)', '<p><span style=\"font-size: 14pt;\"><strong>What to expects :&nbsp;</strong></span></p>\r\n<p>1. Destination Kelingking Secret Point, Broken beach, Angel billabong, Snorkling at Mega<br>Ponton Quicksilver and beach club<br>2. Return fast boat transfers<br>3. Entrance fee<br>4. Lunch Set menu at Local Restaurant<br>5. Private car and driver (max 6 pax / car)</p>\r\n<p>Schedule : - departure 07.30 a.m ( serangan port )<br>- return 15.00 p.m ( penida port )</p>', 'PENIDA WEST TOUR - SNORKLING ( at QUICKSILVER MEGA PONTON)', 580000.00, 'images/post1.webp', 1, '2025-07-03 12:28:04', '2025-07-03 12:28:04', NULL, NULL, NULL, NULL);
